function Levenshtein_distance (s, t) {
    if (s.length == 0) return t.length;
    if (t.length == 0) return s.length;
    var v0 = new Array(t.length + 1);
    var v1 = new Array(t.length + 1);
    for (var i = 0; i < v0.length; i++)
        v0[i] = i;
    for (i = 0; i < s.length; i++) {
        v1[0] = i + 1;
        for (var j = 0; j < t.length; j++) {
            var cost = (s[i] == t[j]) ? 0 : 1;
            v1[j + 1] = Math.min(v1[j] + 1, v0[j + 1] + 1, v0[j] + cost);
        }
        for (j = 0; j < v0.length; j++)
            v0[j] = v1[j];
    }
    return v1[t.length];
}

function getText(element) {
    return (element.innerText || element.textContent);
}

function formatText(text) {
    return text.replace(/^\s+|\s+$/gm,'').replace(/(\r\n|\n|\r)/gm,' ').replace(/\s\s+/gm,' ');
}

function spanToTheme(span) {
    return formatText(getText(span));
}

function tableToQuestion(table) {
    var cells = table.getElementsByTagName('td');
    var question = '';
    for (var i=0; i < cells.length; i++) {
        var children = cells[i].childNodes;
        for (var j=0; j < children.length; j++)
            question = question.concat(getText(children[j]));
    }
    question = formatText(question);
    return question;
}

function getTheme() {
    var THEME_SPAN_INDEX = 0;
    var span = document.getElementsByTagName('span');
    return spanToTheme(span[THEME_SPAN_INDEX]);
}

function getQuestion() {
    var QUESTION_TABLE_INDEX = 1;
    var tables = document.getElementsByTagName('table');
    return tableToQuestion(tables[QUESTION_TABLE_INDEX]);
}

function findCategoriesIndex(theme) {
    var categories = answersJSON().categories;
    var index = 0;
    var levenshtein = Levenshtein_distance(theme, categories[index].name);
    for (var i=1; i < categories.length; i++) {
        var _levenshtein = Levenshtein_distance(theme, categories[i].name);
        if (_levenshtein < levenshtein) {
            index = i;
            levenshtein = _levenshtein;
        }
    }
    return index;
}

function findAnswer(question, categories_index) {
    var questions = answersJSON().categories[categories_index].questions;
    var index = 0;
    var levenshtein = Levenshtein_distance(question, questions[index].question);
    for (var i=1; i < questions.length; i++) {
        var _levenshtein = Levenshtein_distance(question, questions[i].question);
        if (_levenshtein < levenshtein) {
            index = i;
            levenshtein = _levenshtein;
        }
    }
    return questions[index].answer;
}

function getRadioAnswer() {
    var radioanswers =  document.getElementsByName('givenanswer');
    var result = [];
    for (var i=0; i < radioanswers.length; i++) {
        var value = getText(radioanswers[i].parentNode.parentNode.getElementsByClassName('smallfont')[0]);
        result.push({value: value, radio: radioanswers[i]});
    }
    return result;
}

function getTextarea() {
    return document.getElementsByTagName('textarea')[0];
}

function selectOrWriteAnswer(answer) {
    var radio_answer = getRadioAnswer();
    if (radio_answer.length == 0) {
        getTextarea().value = answer;
    } else {
        var index = 0;
        var levenshtein = Levenshtein_distance(answer, radio_answer[index].value);
        for (var i=1; i < radio_answer.length; i++) {
            var _levenshtein = Levenshtein_distance(answer, radio_answer[i].value);
            if (_levenshtein < levenshtein) {
                index = i;
                levenshtein = _levenshtein;
            }
        }
        radio_answer[index].radio.checked = true;
    }
}

(function() {
    var theme = getTheme();
    var categories_index = findCategoriesIndex(theme);
    var question = getQuestion();
    var answer = findAnswer(question, categories_index);
    selectOrWriteAnswer(answer);
})();